import React, { Component } from "react";
import { Link } from "react-router-dom";
const Portfolio_image = <img src="/assets/9.jpg" alt="" />;
const Portfolio_image2 = <img src="/assets/10.jpg" alt="" />;
const Portfolio_image3 = <img src="/assets/11.jpg" alt="" />;

const PortfolioListContent = [
  {
    image: Portfolio_image,

    title: "Arvis PPS",
    description:
      "ARVIS PORTABLE POWER STATION (PPS) is an innovative solar power station that combines a battery for solar energy storage with an inverter that serves as a generator.  ",
  },
  {
    image: Portfolio_image2,

    description: "Lorem ipsum dolor sit amet, consec tetur adipiscing elit.",
  },
  {
    image: Portfolio_image3,
    title: "Photoshop Design",
    description: "Lorem ipsum dolor sit amet, consec tetur adipiscing elit.",
  },
];

class PortfolioList extends Component {
  render() {
    const { column, styevariation } = this.props;
    const list = PortfolioListContent.slice(0, this.props.item);
    return (
      <React.Fragment>
        {list.map((value, index) => (
          <div className={`${column}`} key={index}>
            <div className={`im_portfolio ${styevariation}`}>
              <div className="thumbnail_inner">
                <div className="thumbnail">
                  <Link to="/portfolio-details">{value.image}</Link>
                </div>
              </div>
              <div className="content">
                <div className="inner">
                  <div className="portfolio_heading">
                    <h4 className="title">
                      <Link to="/portfolio-details">{value.title}</Link>
                    </h4>
                  </div>
                  <div className="portfolio_hover">
                    <p>{value.description}</p>
                  </div>
                </div>
              </div>
              <Link className="transparent_link" to="/portfolio-details"></Link>
            </div>
          </div>
        ))}
      </React.Fragment>
    );
  }
}
export default PortfolioList;
